(()=>{"use strict";(t=>{const e=document.getElementById("timer-hours"),n=document.getElementById("timer-minutes"),o=document.getElementById("timer-seconds"),s=()=>{let t=(()=>{let t=(new Date("21 april 2024").getTime()-(new Date).getTime())/1e3;return{timeRemaining:t,hours:Math.floor(t/60/60),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=t.hours,n.textContent=t.minutes,o.textContent=t.seconds;const m=function(t){return t<=9?"0"+t:t};e.textContent=m(t.hours),n.textContent=m(t.minutes),o.textContent=m(t.seconds),t.timeRemaining>0&&setTimeout(s,1e3)};setInterval(s,1e3)})()})();